            'way' => 0
        ];

        $config = array_merge($default,$args);

        $this->name = $config['name'];

        $this->length = $config['length'];

        $this->language = in_array($config['language'],['chinese','english'])?$config['language']:'chinese';

        $this->specialStringNumber = is_int($config['specialStringNumber']) && $config['specialStringNumber'] > 0 && $config['specialStringNumber']<$this->length ? $config['specialStringNumber'] : 0;
        $this->way = $config['way']<count($this->tips)?$config['way']:0;

        if($this->language === 'english'){
            $this->way = 0;
        }


    }


    protected function dot(){

        for($i=0;$i<$this->dotNumber;$i++){
            $color = imagecolorallocate($this->canvas,rand(100,200),rand(100,200),rand(100,200));
            $x = $this->width-1;
            $y = $this->height-1;
            imagesetpixel($this->canvas,rand(1,$x),rand(1,$y),$color);
        }

    }

    protected function line(){
        if($this->lineNumber > 0){
            for($i=0;$i<$this->lineNumber;$i++){
                $color = imagecolorallocate($this->canvas,rand(100,200),rand(100,200),rand(100,200));
                $x = $this->width-1;
                $y = $this->height-1;
                imageline($this->canvas,rand(1,$x),rand(1,$y),rand(1,$x),rand(1,$y),$color);
            }
        }
    }

    //创建画布
    protected function createCanvas(){
        $this->canvas = @imagecreatetruecolor($this->width, $this->height)
        or die('Cannot Initialize new GD image stream');
        $white = imagecolorallocate($this->canvas, 255,255,255);
        $blue = imagecolorallocate($this->canvas, mt_rand(100,255), mt_rand(100,255), mt_rand(100,255));
        $pink = imagecolorallocate($this->canvas, mt_rand(100,255), mt_rand(100,255), mt_rand(100,255));
        $bg = imagecolorallocate($this->canvas, mt_rand(100,255), mt_rand(100,255), mt_rand(100,255));
        imagefill($this->canvas,0,0,$white);
    }

    //返回倒立字体的索引
    protected function specialStringIndex(){

        $specialString = [];
        for($j=0;$j<$this->specialStringNumber;$j++){
            $specialString[] = mt_rand(0,$this->length-1);
        }


        //如果出现的随机数重复 则数组长度为1 再次随机
        $count = count(array_count_values($specialString));

        if($count <$this->specialStringNumber){
            return $this->specialStringIndex();
        }else{
            return $specialString;
        }
    }

    //字符串
    protected function getString(){
        $string = '';
        if($this->language === 'chinese'){
            $sourceString = $this->chineseString;
        }else{
            $sourceString = $this->numberWithLetter;
        }


        for($i=0;$i<$this->length;$i++){
            $string .= mb_substr($sourceString,mt_rand(0,mb_strlen($sourceString)-1),1);
        }
        return $string;
    }

    //渲染字符串
    protected function createString(){
        $specialStringArr = $this->specialStringIndex();

        $finalString = $this->getString();
        $stringLength = mb_strlen($this->getString());

        $sessionString = '';
        $finalSessionString = '';

        if($this->way === 0){
            $finalSessionString = $finalString;
        }


        //输入非倒立的文字 把非倒立的文字存入session
        if($this->way === 1){
            $finalSessionString = $finalString;

            for($i=0;$i<$stringLength;$i++){

                foreach ($specialStringArr as $index){
                    if(mb_substr($finalString,$i,1) === mb_substr($finalString,$index,1)){
                        $sessionString .= mb_substr($finalString,$i,1);
                    }
                }
            }

            for($j=0;$j<$this->specialStringNumber;$j++){
                //去除倒立的文字 剩余的非倒立的文字
                $finalSessionString = str_replace(mb_substr($sessionString,$j,1),'',$finalSessionString);
            }
        }

        if($this->way === 2){
            $finalSessionString = [];
        }

        $font = __DIR__.'/font/c.ttf';

        for($i=0;$i<$this->length;$i++){
            $color = imagecolorallocate($this->canvas,rand(0,150),rand(0,150),rand(0,150));
            $angle = 0;
            $x = ($this->width/$this->length)*$i;
            $y = 25+20;
            if($this->language === 'chinese'){
                if($this->specialStringNumber > 0){
                    //有倒立文字
                    if(in_array($i,$specialStringArr)){
                        $angle = 180;

                        $x = ($this->width/$this->length)*($i+1);

                        $y = $this->height-$this->fontSize-13;

                        if($this->way === 2){
                            //存入倒立文字的x和y轴坐标
                            $fontSizeHalfX = $this->fontSize/2;
                            $fontSizeHalfY = $this->fontSize/2+10;
                            //x轴的最小值
                            $finalXMin = $x-$fontSizeHalfX-$this->fontSize;
                            //x轴最大值
                            $finalXMax = $x+$fontSizeHalfX;

                            $finalYMin = $y-$fontSizeHalfY;
                            $finalYMax = $y+$fontSizeHalfY;

                            $finalSessionString[] = [
                                'x' => [
                                    'min' => $finalXMin,
                                    'max' => $finalXMax
                                ],
                                'y' =>[
                                    'min' => $finalYMin,
                                    'max' => $finalYMax
                                ]
                            ];
                        }
                    }
                }
            }



            $w = mb_substr($finalString,$i,1);

            imagettftext($this->canvas, $this->fontSize, $angle, $x, $y, $color, $font, mb_convert_encoding($w,  'html-entities', 'UTF-8'));

        }

        $color = imagecolorallocate($this->canvas,0,0,0);


        imagettftext($this->canvas, 12, 0, 0, 15, $color, $font, mb_convert_encoding($this->tips[$this->way],  'html-entities', 'UTF-8'));


        $this->saveSession($finalSessionString);
    }

    //存取session
    protected function saveSession($sessionData){
        $_SESSION[$this->name] = $sessionData;

    }

    public function getSession($name = ""){

        if($name === ""){
            $name = $this->name;
        }
        return isset($_SESSION[$name])?$_SESSION[$name]:false;
    }

    /**
     * @param $captcha 1 传过来的验证码数据
     * @param $name 2 session名称
     * @param $way 3 验证方式
     * @return bool
     */
     public function checkSession($captcha,$name){

         if($captcha === '' || $captcha === NULL){
             return false;
         }
         $theSession = $this->getSession($name);

         if(!$theSession){
             return false;
         }
         //如果session是数组  则进行 坐标验证 否则 进行
         if(is_array($theSession)){
             /**
              * 点击验证 数据格式 x1-y1,x2-y2,x3-y3
              */

             $result = 0;
             $captcha = explode(',',$captcha);

             if(count($captcha) > count($theSession)){
                 return false;
             }

             foreach($theSession as $key => $value){
                 $theData = explode('-',$captcha[$key]);
                 if($theData[0] > $theSession[$key]['x']['min'] && $theData[0] < $theSession[$key]['x']['max']){
                     if($theData[1] > $theSession[$key]['y']['min'] && $theData[1] < $theSession[$key]['y']['max']){
                         $result++;
                     }
                 }
             }

             if($result === count($theSession)){

                 $bool = true;
             }else{
                 $bool = false;
             }
         }else{
             if(strtolower($theSession) === strtolower($captcha)){
                 $bool = true;
             }else{
                 $bool = false;
             }
         }

         return $bool;
    }
    //清除session
    protected function clearSession($name){

        unset($_SESSION[$name]);
    }

    public function output(){
        //创建画布
        $this->createCanvas();

        //创建点
        $this->dot();

        //创建线
        $this->line();

        //文字
        $this->createString();


        header("Content-Type:image/png");
        imagepng($this->canvas);
    }

}
